import React, { useContext } from 'react';
import { NavigationContext } from '../slider/navigationContext';
import { NavLink } from '../slider/navigate';

import './nav.css';

const Nav = () => {
  const [navigation, setNavigation] = useContext(NavigationContext);

  const { slug } = navigation;

  return (
    <header className="page-header">
      <nav>
        <Navigate className={slug === '' ? 'selected' : null} href="/">
          index
        </Navigate>
        <Navigate
          className={slug === 'page-two' ? 'selected' : null}
          href="/page-two"
        >
          page-two
        </Navigate>
        <Navigate
          className={slug === 'page-three' ? 'selected' : null}
          href="/page-three"
        >
          page-three
        </Navigate>
      </nav>
    </header>
  );
};

export default Nav;
