import React from 'react';
import './select.scss';

const Select = ({ options = [], onChange }) => {
  const renderOptions = () => {
    return options.map(({ value, label, selected }) => {
      return (
        <option selected={selected} value={value}>
          {label}
        </option>
      );
    });
  };

  return (
    <select
      onChange={event => {
        onChange(event.currentTarget.value);
      }}
    >
      {renderOptions()}
    </select>
  );
};

export default Select;
