import React from 'react';
import './main.css';

const Lettering = ({ text = [] }) => {
  const renderText = () => {
    return text && text.length
      ? text.map(line => {
          return <p>{line}</p>;
        })
      : null;
  };

  return (
    <div>
      <h1>{title}</h1>
      {renderText()}
    </div>
  );
};

export default Lettering;
