import React, { useContext } from 'react';
import AwesomeSlider from 'react-awesome-slider';
import 'react-awesome-slider/dist/styles.css';
import { NavigationContext } from '../components/navigationContext';
import { media } from './config.js';

const Slide = () => {
  const [navigation, setNavigation] = useContext(NavigationContext);

  const handleTransitionStart = () => {
    console.log(`goto: ${navigation.goto}`);
    console.log(`slug: ${navigation.slug}`);

    setNavigation({
      ...navigation,
      slug: navigation.goto,
      navigating: true,
    });
  };

  const handleTransitionEnd = element => {
    const state = {
      ...navigation,
      navigating: false,
    };

    if (element.currentMedia.slug !== navigation.goto) {
      state.slug = element.currentMedia.slug;
      state.goto = element.currentMedia.slug;
    }

    setNavigation(state);

    if (typeof window !== 'undefined') {
      window.history.pushState({}, '', `/${element.currentMedia.slug}`);
    }
  };

  const handleTransitionReject = element => {
    console.log('-- HANDLE REJECT --');
    console.log(element);
    console.log(navigation);
    if (navigation.navigate === false) {
      // something is wrong:
      // THEN ..
      console.log('YES, SOMETHING IS WRONG SO WERE DOING THINGS.');
      setNavigation({
        slug: element.currentMedia.slug,
        goto: element.currentMedia.slug,
        navigating: false,
      });
    }
    // setNavigation({
    //   ...navigation,
    //   goto: navigation.slug,
    //   navigating: false,
    // });
  };

  return (
    <AwesomeSlider
      selected={navigation.goto}
      className="awesome-slider"
      bullets={false}
      fillParent
      media={media}
      infinite={false}
      onTransitionReject={handleTransitionReject}
      onTransitionStart={handleTransitionStart}
      onTransitionEnd={handleTransitionEnd}
    />
  );
};

export default Slide;
