import React from 'react';
import AwesomeSlider from 'react-awesome-slider';
import 'react-awesome-slider/dist/styles.css';
import Main from '../components/main';
import Lettering from '../components/lettering';
import Slide from '../components/slide';
import './slider.css';

const media = [
  {
    backgroundColor: 'red',
    className: 'slide',
    children: (
      <Slide>
        <Lettering
          text={[
            'This is the first line of text',
            'This is the second line of thext',
            'This is the third line of text',
          ]}
        />
      </Slide>
    ),
  },
  {
    className: 'slide',
    children: (
      <Lettering
        text={[
          'This is the first line of text',
          'This is the second line of thext',
          'This is the third line of text',
        ]}
      />
    ),
  },
  {
    className: 'slide',
    children: (
      <Lettering
        text={[
          'This is the first line of text',
          'This is the second line of thext',
          'This is the third line of text',
        ]}
      />
    ),
  },
];

const Home = () => (
  <Main>
    <AwesomeSlider bullets={false} fillParent media={media} />
  </Main>
);

export default Home;
