import React from 'react';
import './select.scss';

const Select = ({ options }) => {
  const renderOptions = () => {
    options.map(({ value, label, selected }) => {
      return (
        <option selected={selected} value={value}>
          {label}
        </option>
      );
    });
  };

  return <select>{renderOptions()}</select>;
};

export default Select;
