import React, { useContext } from 'react';
import { AwesomeButton } from 'react-awesome-button';
import 'react-awesome-button/dist/themes/theme-c137.css';
import { NavigationContext } from './navigationContext';
import Lettering from '../lettering/lettering';
import Mouse from '../mouse/mouse';
import Section from '../section/section';
import Page from '../page/page';
import './slider.css';

export const Home = () => {
  const [navigation, setNavigation] = useContext(NavigationContext);

  return (
    <Section backgroundColor="#ff6f5e">
      <Lettering
        title="INDEX"
        text={['This is a single full page fixed screen.']}
      />
      <div className="button">
        <AwesomeButton
          size="medium"
          onPress={() => {
            setNavigation('/page-two');
          }}
        >
          Next Page
        </AwesomeButton>
      </div>
    </Section>
  );
};

export const media = [
  {
    slug: '',
    className: 'slide page-one',
    children: <Home />,
  },
  {
    slug: 'page-two',
    className: 'sectioned page-two',
    children: (
      <Page>
        <Section backgroundColor="#617be3">
          <Lettering
            title="PAGE-TWO"
            text={[
              'This is multiple section page, scroll down to view more content.',
            ]}
          />
          <Mouse />
        </Section>
        <Section backgroundColor="#4158b4">
          <Lettering
            title="PAGE-SECTION"
            text={['This is a continued page section.']}
          />
        </Section>
      </Page>
    ),
  },
  {
    slug: 'page-three',
    url: 'https://caferati.me/images/series/bojack-0.png',
    className: 'slide page-three',
    children: (
      <Lettering
        title="PAGE-THREE"
        text={['Screen with preloaded background image.']}
      />
    ),
  },
];
