import React from 'react';
import './main.css';

const Lettering = ({ children, text }) => {
  const renderText = () => {
    return text && text.length
      ? text.map(line => {
          return <p>{line}</p>;
        })
      : null;
  };
  return <div>{renderText()}</div>;
};

export default Lettering;
