import React, { useContext } from 'react';
import { NavigationContext } from '../slider/context';
import { NavLink } from '../slider/navigate';
import ReactLogo from '../react-logo/react-logo';
import './nav.css';

const Nav = () => {
  const [navigation, setNavigation] = useContext(NavigationContext);

  const { slug } = navigation;

  return (
    <header className="page-header">
      <div className="page-header__title">
        <ReactLogo />
        <div>
          <h1>
            <span>REACT</span>
            <span>AWESOME SLIDER</span>
          </h1>
          <h2>FULL-PAGE STRATEGIES</h2>
        </div>
      </div>
      <nav>
        <NavLink className={slug === '' ? 'selected' : null} href="/">
          index
        </NavLink>
        <NavLink
          className={slug === 'page-two' ? 'selected' : null}
          href="/page-two"
        >
          page-two
        </NavLink>
        <NavLink
          className={slug === 'page-three' ? 'selected' : null}
          href="/page-three"
        >
          page-three
        </NavLink>
      </nav>
    </header>
  );
};

export default Nav;
